import React, { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useProfile } from "@/hooks/useProfile";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { GroupWithMembers } from "@shared/schema";
import { Users, CreditCard, Calendar, User, Tag } from "lucide-react";

const expenseSchema = z.object({
  amount: z.string().min(1, "Amount is required").refine(
    (val) => !isNaN(Number(val)) && Number(val) > 0,
    "Amount must be a positive number"
  ),
  description: z.string().min(1, "Description is required"),
  category: z.string().default("other"),
  date: z.string().min(1, "Date is required"),
  paidBy: z.string().min(1, "Paid by is required"),
  groupId: z.string().optional(),
  isShared: z.boolean().default(false),
});

type ExpenseFormData = z.infer<typeof expenseSchema>;

interface AddExpenseModalProps {
  isOpen: boolean;
  onClose: () => void;
  groups: GroupWithMembers[];
}

export default function AddExpenseModal({ isOpen, onClose, groups }: AddExpenseModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { profile } = useProfile();
  const [selectedMembers, setSelectedMembers] = useState<string[]>([]);

  const form = useForm<ExpenseFormData>({
    resolver: zodResolver(expenseSchema),
    defaultValues: {
      amount: "",
      description: "",
      category: "other",
      date: new Date().toISOString().split('T')[0],
      paidBy: profile?.publicName || "",
      groupId: "",
      isShared: false,
    },
  });

  // Update default values when profile loads
  React.useEffect(() => {
    if (profile?.publicName && !form.getValues().paidBy) {
      form.setValue("paidBy", profile.publicName);
    }
  }, [profile, form]);

  const createExpenseMutation = useMutation({
    mutationFn: async (data: ExpenseFormData) => {
      return await apiRequest("POST", "/api/transactions", {
        ...data,
        type: "expense",
        amount: data.amount,
        isShared: data.isShared,
        groupId: data.isShared ? data.groupId : null,
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Expense added successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/stats/monthly"] });
      form.reset({
        amount: "",
        description: "",
        category: "other",
        date: new Date().toISOString().split('T')[0],
        paidBy: profile?.publicName || "",
        groupId: "",
        isShared: false,
      });
      setSelectedMembers([]);
      onClose();
    },
    onError: (error) => {
      console.error('Expense creation error:', error);
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to add expense",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ExpenseFormData) => {
    createExpenseMutation.mutate(data);
  };

  const expenseCategories = [
    { value: "other", label: "üìã Other", description: "Miscellaneous expenses (default)" },
    { value: "food", label: "üçΩÔ∏è Food & Dining", description: "Restaurants, groceries, takeout" },
    { value: "utilities", label: "‚ö° Utilities", description: "Electricity, gas, water, internet" },
    { value: "entertainment", label: "üé¨ Entertainment", description: "Movies, games, events" },
    { value: "transportation", label: "üöó Transportation", description: "Fuel, public transport, rides" },
    { value: "shopping", label: "üõçÔ∏è Shopping", description: "Clothes, electronics, household items" },
    { value: "healthcare", label: "üè• Healthcare", description: "Doctor visits, medicines, insurance" },
    { value: "education", label: "üìö Education", description: "Books, courses, tuition" },
    { value: "rent", label: "üè† Rent/Housing", description: "Monthly rent, maintenance" },
    { value: "travel", label: "‚úàÔ∏è Travel", description: "Vacation, business trips" },
    { value: "subscription", label: "üì± Subscriptions", description: "Netflix, Spotify, software" },
  ];

  const isShared = form.watch("isShared");
  const selectedGroupId = form.watch("groupId");
  const selectedGroup = groups.find(g => g.id === selectedGroupId);

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-md max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <div className="p-2 bg-red-100 rounded-lg">
              <CreditCard className="h-5 w-5 text-red-600" />
            </div>
            Add Expense
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="amount"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="flex items-center gap-2">
                    <CreditCard className="h-4 w-4 text-red-600" />
                    Amount (‚Ç®)
                  </FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      type="number"
                      step="0.01"
                      placeholder="Enter amount..."
                      className="text-lg font-semibold"
                      data-testid="input-expense-amount"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="category"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="flex items-center gap-2">
                    <Tag className="h-4 w-4 text-blue-600" />
                    Category
                  </FormLabel>
                  <Select onValueChange={field.onChange} value={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-expense-category">
                        <SelectValue placeholder="Other (default category)" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {expenseCategories.map((category) => (
                        <SelectItem key={category.value} value={category.value}>
                          <div>
                            <div className="font-medium">{category.label}</div>
                            <div className="text-sm text-gray-500">{category.description}</div>
                          </div>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description</FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      placeholder="e.g., Lunch at restaurant, grocery shopping..."
                      data-testid="input-expense-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="date"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="flex items-center gap-2">
                    <Calendar className="h-4 w-4 text-gray-600" />
                    Date
                  </FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      type="date"
                      data-testid="input-expense-date"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="paidBy"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="flex items-center gap-2">
                    <User className="h-4 w-4 text-purple-600" />
                    Paid By
                  </FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      placeholder="Enter name..."
                      data-testid="input-expense-paid-by"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {groups.length > 0 && (
              <FormField
                control={form.control}
                name="isShared"
                render={({ field }) => (
                  <FormItem className="flex flex-row items-center justify-between rounded-lg border p-3">
                    <div className="space-y-0.5">
                      <FormLabel className="flex items-center gap-2">
                        <Users className="h-4 w-4 text-blue-600" />
                        Shared Expense
                      </FormLabel>
                      <div className="text-sm text-gray-500">
                        Split this expense with a group
                      </div>
                    </div>
                    <FormControl>
                      <Switch
                        checked={field.value}
                        onCheckedChange={field.onChange}
                        data-testid="switch-shared-expense"
                      />
                    </FormControl>
                  </FormItem>
                )}
              />
            )}

            {isShared && (
              <FormField
                control={form.control}
                name="groupId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Select Group</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-group">
                          <SelectValue placeholder="Choose a group" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {groups.map((group) => (
                          <SelectItem key={group.id} value={group.id}>
                            <div>
                              <div className="font-medium">{group.name}</div>
                              <div className="text-sm text-gray-500">
                                {group.memberCount} members
                              </div>
                            </div>
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            )}

            {isShared && selectedGroup && (
              <div className="p-3 bg-blue-50 rounded-lg">
                <h4 className="font-medium text-blue-900 mb-2">Split Details</h4>
                <p className="text-sm text-blue-700">
                  This expense will be split equally among {selectedGroup?.memberCount || 0} members of "{selectedGroup?.name}"
                </p>
                {form.getValues().amount && selectedGroup && (
                  <p className="text-sm font-medium text-blue-800 mt-1">
                    Each member owes: ‚Ç® {(parseFloat(form.getValues().amount || "0") / (selectedGroup.memberCount || 1)).toFixed(2)}
                  </p>
                )}
              </div>
            )}

            <div className="flex gap-3 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={onClose}
                className="flex-1"
                data-testid="button-cancel-expense"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createExpenseMutation.isPending}
                className="flex-1 bg-gradient-to-r from-red-600 to-rose-600 hover:from-red-700 hover:to-rose-700"
                data-testid="button-add-expense"
              >
                {createExpenseMutation.isPending ? "Adding..." : "Add Expense"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/AddGroupModal.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Trash2, Plus } from "lucide-react";

const groupSchema = z.object({
  name: z.string().min(1, "Group name is required"),
  description: z.string().optional(),
});

type GroupFormData = z.infer<typeof groupSchema>;

interface AddGroupModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export default function AddGroupModal({ isOpen, onClose }: AddGroupModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [members, setMembers] = useState<{ name: string; email?: string }[]>([
    { name: "", email: "" }
  ]);

  const form = useForm<GroupFormData>({
    resolver: zodResolver(groupSchema),
    defaultValues: {
      name: "",
      description: "",
    },
  });

  const createGroupMutation = useMutation({
    mutationFn: async (data: GroupFormData) => {
      // Create the group first
      const group = await apiRequest("POST", "/api/groups", data) as any;

      // Add members to the group
      const validMembers = members.filter(member => member.name.trim());
      if (validMembers.length > 0) {
        await Promise.all(
          validMembers.map(member =>
            apiRequest("POST", `/api/groups/${group.id}/members`, {
              name: member.name.trim(),
              email: member.email?.trim() || null,
            })
          )
        );
      }

      return group;
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Group created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/groups"] });
      form.reset();
      setMembers([{ name: "", email: "" }]);
      onClose();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: "Failed to create group. Please try again.",
        variant: "destructive",
      });
    },
  });

  const addMember = () => {
    setMembers([...members, { name: "", email: "" }]);
  };

  const removeMember = (index: number) => {
    if (members.length > 1) {
      setMembers(members.filter((_, i) => i !== index));
    }
  };

  const updateMember = (index: number, field: 'name' | 'email', value: string) => {
    const updatedMembers = [...members];
    updatedMembers[index] = { ...updatedMembers[index], [field]: value };
    setMembers(updatedMembers);
  };

  const onSubmit = (data: GroupFormData) => {
    createGroupMutation.mutate(data);
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md" data-testid="modal-add-group">
        <DialogHeader>
          <DialogTitle>Create New Group</DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Group Name</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="Enter group name"
                      {...field}
                      data-testid="input-group-name"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description (Optional)</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="What's this group for?"
                      {...field}
                      data-testid="input-group-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <Label>Group Members</Label>
                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={addMember}
                  data-testid="button-add-member"
                >
                  <Plus className="w-4 h-4 mr-1" />
                  Add Member
                </Button>
              </div>

              <div className="space-y-2 max-h-48 overflow-y-auto">
                {members.map((member, index) => (
                  <div key={index} className="flex items-center space-x-2">
                    <Input
                      placeholder="Member name"
                      value={member.name}
                      onChange={(e) => updateMember(index, 'name', e.target.value)}
                      className="flex-1"
                      data-testid={`input-member-name-${index}`}
                    />
                    <Input
                      placeholder="Email (optional)"
                      value={member.email || ''}
                      onChange={(e) => updateMember(index, 'email', e.target.value)}
                      className="flex-1"
                      data-testid={`input-member-email-${index}`}
                    />
                    <Button
                      type="button"
                      variant="outline"
                      size="sm"
                      onClick={() => removeMember(index)}
                      disabled={members.length === 1}
                      data-testid={`button-remove-member-${index}`}
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                ))}
              </div>
            </div>

            <div className="flex space-x-3 pt-4">
              <Button 
                type="button" 
                variant="outline" 
                className="flex-1" 
                onClick={onClose}
                data-testid="button-cancel-group"
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                className="flex-1 bg-blue-500 hover:bg-blue-600 text-white"
                disabled={createGroupMutation.isPending}
                data-testid="button-submit-group"
              >
                {createGroupMutation.isPending ? "Creating..." : "Create Group"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

