import React from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useProfile } from "@/hooks/useProfile";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { DollarSign, Calendar, User, Tag } from "lucide-react";

const incomeSchema = z.object({
  amount: z.string().min(1, "Amount is required").refine(
    (val) => !isNaN(Number(val)) && Number(val) > 0,
    "Amount must be a positive number"
  ),
  description: z.string().min(1, "Source is required"),
  date: z.string().min(1, "Date is required"),
  paidBy: z.string().min(1, "Received by is required"),
  category: z.string().default("other"),
});

type IncomeFormData = z.infer<typeof incomeSchema>;

interface AddIncomeModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export default function AddIncomeModal({ isOpen, onClose }: AddIncomeModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { profile } = useProfile();

  const form = useForm<IncomeFormData>({
    resolver: zodResolver(incomeSchema),
    defaultValues: {
      amount: "",
      description: "",
      date: new Date().toISOString().split('T')[0],
      paidBy: profile?.publicName || "",
      category: "other",
    },
  });

  // Update default values when profile loads
  React.useEffect(() => {
    if (profile?.publicName && !form.getValues().paidBy) {
      form.setValue("paidBy", profile.publicName);
    }
  }, [profile, form]);

  const createIncomeMutation = useMutation({
    mutationFn: async (data: IncomeFormData) => {
      return await apiRequest("POST", "/api/transactions", {
        ...data,
        type: "income",
        amount: data.amount,
        category: data.category,
        isShared: false,
        groupId: null,
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Income added successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/stats/monthly"] });
      form.reset({
        amount: "",
        description: "",
        date: new Date().toISOString().split('T')[0],
        paidBy: profile?.publicName || "",
        category: "other",
      });
      onClose();
    },
    onError: (error) => {
      console.error('Income creation error:', error);
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to add income",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: IncomeFormData) => {
    createIncomeMutation.mutate(data);
  };

  const incomeCategories = [
    { value: "other", label: "üìã Other", description: "Other income sources (default)" },
    { value: "salary", label: "üíº Salary/Wages", description: "Regular employment income" },
    { value: "freelance", label: "üíª Freelance", description: "Project-based work" },
    { value: "business", label: "üè¢ Business", description: "Business revenue" },
    { value: "investment", label: "üìà Investment", description: "Stocks, dividends, interest" },
    { value: "rental", label: "üè† Rental", description: "Property rental income" },
    { value: "bonus", label: "üéÅ Bonus", description: "Performance bonuses" },
    { value: "gift", label: "üéâ Gift", description: "Money received as gift" },
    { value: "refund", label: "üîÑ Refund", description: "Money returned" },
  ];

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <div className="p-2 bg-green-100 rounded-lg">
              <DollarSign className="h-5 w-5 text-green-600" />
            </div>
            Add Income
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="amount"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="flex items-center gap-2">
                    <DollarSign className="h-4 w-4 text-green-600" />
                    Amount (‚Ç®)
                  </FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      type="number"
                      step="0.01"
                      placeholder="Enter amount..."
                      className="text-lg font-semibold"
                      data-testid="input-income-amount"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="category"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="flex items-center gap-2">
                    <Tag className="h-4 w-4 text-blue-600" />
                    Category
                  </FormLabel>
                  <Select onValueChange={field.onChange} value={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-income-category">
                        <SelectValue placeholder="Other (default category)" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {incomeCategories.map((category) => (
                        <SelectItem key={category.value} value={category.value}>
                          <div>
                            <div className="font-medium">{category.label}</div>
                            <div className="text-sm text-gray-500">{category.description}</div>
                          </div>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description/Source</FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      placeholder="e.g., Monthly salary, freelance project..."
                      data-testid="input-income-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="date"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="flex items-center gap-2">
                    <Calendar className="h-4 w-4 text-gray-600" />
                    Date
                  </FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      type="date"
                      data-testid="input-income-date"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="paidBy"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="flex items-center gap-2">
                    <User className="h-4 w-4 text-purple-600" />
                    Received By
                  </FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      placeholder="Enter name..."
                      data-testid="input-income-received-by"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex gap-3 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={onClose}
                className="flex-1"
                data-testid="button-cancel-income"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createIncomeMutation.isPending}
                className="flex-1 bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700"
                data-testid="button-add-income"
              >
                {createIncomeMutation.isPending ? "Adding..." : "Add Income"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ExportButtons.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { FileText, FileSpreadsheet, Download, Eye, TrendingUp } from "lucide-react";
import jsPDF from 'jspdf';
import { format } from 'date-fns';

interface ExportButtonsProps {
  filters: {
    search: string;
    dateRange: string;
    category: string;
    type: string;
    paidBy: string;
    startDate: string;
    endDate: string;
    onlyUser: boolean;
    onlyGroupMembers: boolean;
  };
}

interface Transaction {
  id: string;
  type: 'income' | 'expense';
  amount: string;
  description: string;
  category: string;
  date: string;
  paidBy: string;
  isShared: boolean;
  groupId: string | null;
}

export default function ExportButtons({ filters }: ExportButtonsProps) {
  const [isExporting, setIsExporting] = useState<string | null>(null);
  const [pdfUrl, setPdfUrl] = useState<string | null>(null);
  const { toast } = useToast();

  const fetchFilteredTransactions = async (): Promise<Transaction[]> => {
    const params = new URLSearchParams();
    if (filters.search) params.append('search', filters.search);
    if (filters.category && filters.category !== 'all') params.append('category', filters.category);
    if (filters.type && filters.type !== 'all') params.append('type', filters.type);
    if (filters.paidBy && filters.paidBy !== 'all') params.append('paidBy', filters.paidBy);
    if (filters.startDate) params.append('startDate', filters.startDate);
    if (filters.endDate) params.append('endDate', filters.endDate);

    if (filters.onlyUser) params.append('onlyUser', 'true');
    if (filters.onlyGroupMembers) params.append('onlyGroupMembers', 'true');

    const response = await fetch(`/api/transactions?${params.toString()}`);
    if (!response.ok) throw new Error('Failed to fetch transactions');
    return response.json();
  };

  const generateLedgerPDF = async (transactions: Transaction[]): Promise<string> => {
    const doc = new jsPDF();

    // Sort transactions by date for ledger format
    const sortedTransactions = transactions.sort((a, b) => 
      new Date(a.date).getTime() - new Date(b.date).getTime()
    );

    // Professional header with gradient effect
    doc.setFillColor(37, 99, 235); // Blue gradient start
    doc.rect(0, 0, 210, 35, 'F');

    // Title with white text
    doc.setTextColor(255, 255, 255);
    doc.setFontSize(22);
    doc.setFont('helvetica', 'bold');
    doc.text('Financial Ledger Report', 105, 20, { align: 'center' });

    // Subtitle
    doc.setFontSize(12);
    doc.setFont('helvetica', 'normal');
    doc.text('Income & Expense Tracking', 105, 28, { align: 'center' });

    // Reset text color and add report details
    doc.setTextColor(0, 0, 0);
    let yPos = 50;

    doc.setFontSize(11);
    doc.text(`Generated: ${format(new Date(), 'PPP')}`, 20, yPos);
    yPos += 6;

    if (filters.startDate || filters.endDate) {
      doc.text(`Period: ${filters.startDate || 'Beginning'} to ${filters.endDate || 'Current'}`, 20, yPos);
      yPos += 6;
    }

    doc.text(`Total Records: ${sortedTransactions.length}`, 20, yPos);
    yPos += 15;

    // Ledger table headers
    doc.setFillColor(30, 64, 175); // Dark blue header
    doc.rect(20, yPos, 170, 12, 'F');

    doc.setTextColor(255, 255, 255);
    doc.setFontSize(10);
    doc.setFont('helvetica', 'bold');
    doc.text('Date', 25, yPos + 8);
    doc.text('Description', 50, yPos + 8);
    doc.text('Person', 100, yPos + 8);
    doc.text('Income', 125, yPos + 8);
    doc.text('Expense', 145, yPos + 8);
    doc.text('Balance', 170, yPos + 8);

    yPos += 15;
    doc.setTextColor(0, 0, 0);
    doc.setFont('helvetica', 'normal');
    doc.setFontSize(9);

    // Calculate running balance and create ledger entries
    let runningBalance = 0;
    let rowCount = 0;

    sortedTransactions.forEach((transaction) => {
      if (yPos > 270) { // Add new page if needed
        doc.addPage();
        yPos = 20;

        // Repeat header on new page
        doc.setFillColor(30, 64, 175);
        doc.rect(20, yPos, 170, 12, 'F');
        doc.setTextColor(255, 255, 255);
        doc.setFont('helvetica', 'bold');
        doc.setFontSize(10);
        doc.text('Date', 25, yPos + 8);
        doc.text('Description', 50, yPos + 8);
        doc.text('Person', 100, yPos + 8);
        doc.text('Income', 125, yPos + 8);
        doc.text('Expense', 145, yPos + 8);
        doc.text('Balance', 170, yPos + 8);
        yPos += 15;
        doc.setTextColor(0, 0, 0);
        doc.setFont('helvetica', 'normal');
        doc.setFontSize(9);
      }

      const amount = parseFloat(transaction.amount);

      if (transaction.type === 'income') {
        runningBalance += amount;
      } else {
        runningBalance -= amount;
      }

      // Alternating row background
      if (rowCount % 2 === 0) {
        doc.setFillColor(248, 250, 252);
        doc.rect(20, yPos - 2, 170, 10, 'F');
      }

      // Date
      doc.text(format(new Date(transaction.date), 'MMM dd'), 25, yPos + 5);

      // Description (truncated)
      doc.text(transaction.description.substring(0, 20), 50, yPos + 5);

      // Person (truncated)
      doc.text(transaction.paidBy.substring(0, 10), 100, yPos + 5);

      // Income amount (green)
      if (transaction.type === 'income') {
        doc.setTextColor(5, 150, 105);
        doc.text(amount.toLocaleString(), 125, yPos + 5);
        doc.setTextColor(0, 0, 0);
      }

      // Expense amount (red)
      if (transaction.type === 'expense') {
        doc.setTextColor(220, 38, 38);
        doc.text(amount.toLocaleString(), 145, yPos + 5);
        doc.setTextColor(0, 0, 0);
      }

      // Running balance (green if positive, red if negative)
      if (runningBalance >= 0) {
        doc.setTextColor(5, 150, 105);
      } else {
        doc.setTextColor(220, 38, 38);
      }
      doc.setFont('helvetica', 'bold');
      doc.text(runningBalance.toLocaleString(), 170, yPos + 5);
      doc.setFont('helvetica', 'normal');
      doc.setTextColor(0, 0, 0);

      yPos += 10;
      rowCount++;
    });

    // Final totals section
    yPos += 10;
    doc.setFillColor(243, 244, 246);
    doc.rect(20, yPos, 170, 25, 'F');
    doc.setDrawColor(107, 114, 128);
    doc.rect(20, yPos, 170, 25, 'S');

    const totalIncome = sortedTransactions
      .filter(t => t.type === 'income')
      .reduce((sum, t) => sum + parseFloat(t.amount), 0);
    const totalExpenses = sortedTransactions
      .filter(t => t.type === 'expense')
      .reduce((sum, t) => sum + parseFloat(t.amount), 0);
    const finalBalance = totalIncome - totalExpenses;

    doc.setFont('helvetica', 'bold');
    doc.setFontSize(11);
    doc.text('FINAL SUMMARY', 25, yPos + 8);

    doc.setFontSize(10);
    doc.setTextColor(5, 150, 105);
    doc.text(`Total Income: ‚Ç® ${totalIncome.toLocaleString()}`, 25, yPos + 16);

    doc.setTextColor(220, 38, 38);
    doc.text(`Total Expenses: ‚Ç® ${totalExpenses.toLocaleString()}`, 25, yPos + 21);

    doc.setTextColor(finalBalance >= 0 ? 5 : 220, finalBalance >= 0 ? 150 : 38, finalBalance >= 0 ? 105 : 38);
    doc.setFontSize(12);
    doc.text(`Net Balance: ‚Ç® ${finalBalance.toLocaleString()}`, 120, yPos + 18);

    // Footer
    const pageCount = doc.getNumberOfPages();
    for (let i = 1; i <= pageCount; i++) {
      doc.setPage(i);
      doc.setFontSize(8);
      doc.setTextColor(107, 114, 128);
      doc.text(`ExpenseShare Ledger | Page ${i} of ${pageCount} | ${format(new Date(), 'PPp')}`, 105, 285, { align: 'center' });
    }

    const blobUrl = doc.output('bloburl');
    return typeof blobUrl === 'string' ? blobUrl : URL.createObjectURL(new Blob([doc.output('blob')], { type: 'application/pdf' }));
  };

  const generateExcel = async (transactions: Transaction[]) => {
    const response = await fetch('/api/export/excel', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ 
        transactions,
        filters,
        summary: {
          totalIncome: transactions.filter(t => t.type === 'income').reduce((sum, t) => sum + parseFloat(t.amount), 0),
          totalExpenses: transactions.filter(t => t.type === 'expense').reduce((sum, t) => sum + parseFloat(t.amount), 0),
        }
      }),
    });

    if (!response.ok) {
      throw new Error(`Excel export failed: ${response.statusText}`);
    }

    const blob = await response.blob();
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.style.display = 'none';
    a.href = url;
    a.download = `ledger-report-${format(new Date(), 'yyyy-MM-dd')}.xlsx`;
    document.body.appendChild(a);
    a.click();
    window.URL.revokeObjectURL(url);
    document.body.removeChild(a);
  };

  const handleExport = async (format: 'pdf' | 'excel') => {
    setIsExporting(format);

    try {
      const transactions = await fetchFilteredTransactions();

      if (transactions.length === 0) {
        toast({
          title: "No Data",
          description: "No transactions found matching your filters",
          variant: "destructive",
        });
        return;
      }

      if (format === 'pdf') {
        const pdfUrl = await generateLedgerPDF(transactions);
        setPdfUrl(pdfUrl);

        toast({
          title: "Ledger PDF Ready",
          description: "Your financial ledger is ready to download or preview",
        });
      } else {
        await generateExcel(transactions);

        toast({
          title: "Excel Downloaded",
          description: "Your Excel ledger has been downloaded successfully",
        });
      }
    } catch (error) {
      console.error('Export error:', error);
      toast({
        title: "Export Failed",
        description: error instanceof Error ? error.message : "An error occurred during export",
        variant: "destructive",
      });
    } finally {
      setIsExporting(null);
    }
  };

  const downloadPDF = () => {
    if (pdfUrl) {
      const a = document.createElement('a');
      a.href = pdfUrl;
      a.download = `ledger-report-${format(new Date(), 'yyyy-MM-dd')}.pdf`;
      a.click();
    }
  };

  const previewPDF = () => {
    if (pdfUrl) {
      window.open(pdfUrl, '_blank');
    }
  };

  return (
    <div className="bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-600 p-6 rounded-2xl shadow-2xl mb-6">
      <div className="flex flex-col lg:flex-row items-center justify-between">
        {/* Left side - Title and description */}
        <div className="text-white mb-4 lg:mb-0">
          <div className="flex items-center gap-3 mb-2">
            <div className="p-2 bg-white/20 rounded-full">
              <TrendingUp className="h-6 w-6" />
            </div>
            <h2 className="text-2xl font-bold">Financial Reports</h2>
          </div>
          <p className="text-indigo-100 text-sm">
            Export your financial data in professional ledger format
          </p>
        </div>

        {/* Right side - Export buttons */}
        <div className="flex flex-col sm:flex-row gap-3">
          <Button
            variant="secondary"
            className="bg-white/90 hover:bg-white text-indigo-700 font-semibold px-6 py-3 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105"
            onClick={() => handleExport('pdf')}
            disabled={isExporting === 'pdf'}
            data-testid="button-export-pdf"
          >
            <FileText className="mr-2 h-5 w-5" />
            {isExporting === 'pdf' ? 'Generating PDF...' : 'Ledger PDF'}
          </Button>

          {pdfUrl && (
            <div className="flex gap-2">
              <Button
                variant="secondary"
                size="sm"
                className="bg-emerald-500 hover:bg-emerald-600 text-white font-medium px-4 py-2 rounded-lg shadow-md hover:shadow-lg transition-all duration-200"
                onClick={downloadPDF}
                data-testid="button-download-pdf"
              >
                <Download className="mr-1 h-4 w-4" />
                Download
              </Button>
              <Button
                variant="secondary"
                size="sm"
                className="bg-amber-500 hover:bg-amber-600 text-white font-medium px-4 py-2 rounded-lg shadow-md hover:shadow-lg transition-all duration-200"
                onClick={previewPDF}
                data-testid="button-preview-pdf"
              >
                <Eye className="mr-1 h-4 w-4" />
                Preview
              </Button>
            </div>
          )}

          <Button
            variant="secondary"
            className="bg-white/90 hover:bg-white text-green-700 font-semibold px-6 py-3 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105"
            onClick={() => handleExport('excel')}
            disabled={isExporting === 'excel'}
            data-testid="button-export-excel"
          >
            <FileSpreadsheet className="mr-2 h-5 w-5" />
            {isExporting === 'excel' ? 'Generating Excel...' : 'Ledger Excel'}
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/components/InviteModal.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Copy, Share2, MessageCircle, Users, Clock, Hash, X, Loader2, Plus } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Badge } from "@/components/ui/badge";
import type { GroupInvite, GroupWithMembers } from "@shared/schema";

interface InviteModalProps {
  group: GroupWithMembers;
  children: React.ReactNode;
}

export function InviteModal({ group, children }: InviteModalProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [invitedBy, setInvitedBy] = useState("");
  const [maxUses, setMaxUses] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Set default invited by name when opening
  useEffect(() => {
    if (isOpen && !invitedBy) {
      setInvitedBy("Admin"); // Default name
    }
  }, [isOpen, invitedBy]);

  // Fetch existing invites for the group
  const { data: invites = [], isLoading, error: invitesError } = useQuery({
    queryKey: ['/api/groups', group.id, 'invites'],
    enabled: isOpen,
    retry: (failureCount, error: any) => {
      // Don't retry on auth errors
      if (error?.message?.includes('401') || error?.message?.includes('Unauthorized')) {
        return false;
      }
      return failureCount < 2;
    },
  }) as { data: GroupInvite[], isLoading: boolean, error: any };

  // Create invite mutation
  const createInviteMutation = useMutation({
    mutationFn: async (data: { invitedBy: string; maxUses?: number }) => {
      try {
        console.log("üöÄ Starting invite creation for group:", group.id, "with data:", data);

        // Add credentials to ensure proper session handling
        const response = await fetch(`/api/groups/${group.id}/invites`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include', // Ensure cookies are sent
          body: JSON.stringify(data),
        });

        console.log("üì° Response status:", response.status, response.statusText);

        if (!response.ok) {
          const errorText = await response.text();
          console.error("‚ùå Invite creation failed:", response.status, errorText);
          throw new Error(`HTTP ${response.status}: ${errorText}`);
        }

        const result = await response.json();
        console.log("‚úÖ Invite created successfully:", result);
        return result;
      } catch (error) {
        console.error("üî• Error in invite creation:", error);
        throw error;
      }
    },
    onSuccess: (data) => {
      toast({
        title: "Invite Created Successfully! üéâ",
        description: `Invite link created with code: ${data.inviteCode}`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/groups', group.id, 'invites'] });
      setMaxUses(""); // Keep invitedBy for next invite
    },
    onError: (error: any) => {
      console.error("Invite creation error:", error);

      let errorMessage = "Failed to create invite link. Please try again.";

      if (error.message.includes("401") || error.message.includes("Unauthorized")) {
        errorMessage = "Please refresh the page and try again - your session may have expired.";
      } else if (error.message.includes("403")) {
        errorMessage = "You don't have permission to create invites for this group.";
      } else if (error.message.includes("400")) {
        errorMessage = "Invalid request. Please check your input and try again.";
      }

      toast({
        title: "Invite Creation Failed",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  // Deactivate invite mutation
  const deactivateInviteMutation = useMutation({
    mutationFn: async (inviteId: string) => {
      const response = await apiRequest('PATCH', `/api/invites/${inviteId}/deactivate`);
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`${response.status}: ${errorText}`);
      }
      return await response.json();
    },
    onSuccess: () => {
      toast({
        title: "Invite Deactivated",
        description: "The invite link has been deactivated successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/groups', group.id, 'invites'] });
    },
    onError: (error: any) => {
      console.error("Error deactivating invite:", error);
      toast({
        title: "Deactivation Failed",
        description: "Failed to deactivate the invite. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleCreateInvite = () => {
    console.log("üéØ handleCreateInvite called");
    console.log("üìã Form validation - invitedBy:", invitedBy.trim());

    if (!invitedBy.trim()) {
      console.log("‚ùå Validation failed: No name provided");
      toast({
        title: "Name Required",
        description: "Please enter your name to create an invite.",
        variant: "destructive",
      });
      return;
    }

    const mutationData = {
      invitedBy: invitedBy.trim(),
      maxUses: maxUses ? parseInt(maxUses) : undefined,
    };

    console.log("üöÄ Starting mutation with data:", mutationData);
    createInviteMutation.mutate(mutationData);
  };

  const copyInviteLink = (inviteCode: string) => {
    const inviteUrl = `${window.location.origin}/invite/${inviteCode}`;
    navigator.clipboard.writeText(inviteUrl).then(() => {
      toast({
        title: "Link Copied! üìã",
        description: "Invite link copied to clipboard successfully!",
      });
    }).catch(() => {
      toast({
        title: "Copy Failed",
        description: "Could not copy link. Please copy it manually.",
        variant: "destructive",
      });
    });
  };

  const shareViaWhatsApp = (inviteCode: string) => {
    const inviteUrl = `${window.location.origin}/invite/${inviteCode}`;
    const message = `Join our expense group "${group.name}"! üí∞\n\nClick here to join: ${inviteUrl}\n\nLet's track our expenses together! üéØ`;
    const whatsappUrl = `https://wa.me/?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };

  const shareViaEmail = (inviteCode: string) => {
    const inviteUrl = `${window.location.origin}/invite/${inviteCode}`;
    const subject = `Join "${group.name}" expense group`;
    const body = `Hi there!\n\nYou've been invited to join our expense group "${group.name}" on ExpenseShare.\n\nClick the link below to join:\n${inviteUrl}\n\nThis will help us track shared expenses and split costs easily!\n\nBest regards!`;
    const emailUrl = `mailto:?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
    window.open(emailUrl);
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        {children}
      </DialogTrigger>
      <DialogContent className="sm:max-w-[600px] max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Share2 className="w-5 h-5" />
            Invite Members to "{group.name}"
          </DialogTitle>
          <DialogDescription>
            Create invite links to add new members to this expense group. Members can join without creating an account.
          </DialogDescription>
        </DialogHeader>

        <div className="grid gap-6 py-4">
          {/* Create New Invite */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Create New Invite</CardTitle>
              <CardDescription>Generate a new invite link for this group</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid gap-2">
                <Label htmlFor="invitedBy">Your Name</Label>
                <Input
                  id="invitedBy"
                  value={invitedBy}
                  onChange={(e) => setInvitedBy(e.target.value)}
                  placeholder="Enter your name"
                  data-testid="input-invited-by"
                />
              </div>

              <div className="grid gap-2">
                <Label htmlFor="maxUses">Max Uses (Optional)</Label>
                <Input
                  id="maxUses"
                  type="number"
                  value={maxUses}
                  onChange={(e) => setMaxUses(e.target.value)}
                  placeholder="Leave empty for unlimited"
                  min="1"
                  max="100"
                  data-testid="input-max-uses"
                />
              </div>

              <Button 
                onClick={() => {
                  console.log("üîò Create Invite button clicked!");
                  console.log("üìù Current form values:", { invitedBy, maxUses });
                  handleCreateInvite();
                }}
                disabled={createInviteMutation.isPending || !invitedBy.trim()}
                className="w-full"
                data-testid="button-create-invite"
              >
                {createInviteMutation.isPending ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Creating...
                  </>
                ) : (
                  <>
                    <Plus className="w-4 h-4 mr-2" />
                    Create Invite Link
                  </>
                )}
              </Button>
            </CardContent>
          </Card>

          {/* Existing Invites */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg flex items-center justify-between">
                Active Invites
                <Badge variant="secondary">{invites.length}</Badge>
              </CardTitle>
              <CardDescription>Manage your existing invite links</CardDescription>
            </CardHeader>
            <CardContent>
              {isLoading ? (
                <div className="flex items-center justify-center py-8">
                  <Loader2 className="w-6 h-6 animate-spin" />
                  <span className="ml-2">Loading invites...</span>
                </div>
              ) : invitesError ? (
                <div className="text-center py-8 text-gray-500">
                  <p className="text-red-600">Failed to load invites</p>
                  <p className="text-sm">Please refresh the page and try again</p>
                </div>
              ) : invites.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <Users className="mx-auto h-8 w-8 text-gray-400 mb-2" />
                  <p>No active invites yet</p>
                  <p className="text-sm">Create your first invite above!</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {invites.map((invite: GroupInvite) => (
                    <div key={invite.id} className="p-4 border rounded-lg" data-testid={`invite-${invite.id}`}>
                      <div className="flex items-center justify-between">
                        <div className="space-y-1">
                          <div className="flex items-center gap-2">
                            <Hash className="w-4 h-4" />
                            <code className="text-sm font-mono bg-gray-100 px-2 py-1 rounded">
                              {invite.inviteCode}
                            </code>
                            <Badge variant={invite.isActive ? "default" : "secondary"}>
                              {invite.isActive ? "Active" : "Inactive"}
                            </Badge>
                          </div>
                          <div className="flex items-center gap-4 text-sm text-gray-500">
                            <span>By: {invite.invitedBy}</span>
                            {invite.maxUses && (
                              <span>Limit: {invite.currentUses || 0}/{invite.maxUses}</span>
                            )}
                            <span>
                              <Clock className="w-3 h-3 inline mr-1" />
                              {new Date(invite.createdAt || Date.now()).toLocaleDateString()}
                            </span>
                          </div>
                        </div>

                        {invite.isActive && (
                          <div className="flex items-center gap-2">
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => copyInviteLink(invite.inviteCode)}
                              data-testid={`button-copy-${invite.id}`}
                            >
                              <Copy className="w-3 h-3" />
                            </Button>
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => shareViaWhatsApp(invite.inviteCode)}
                              data-testid={`button-whatsapp-${invite.id}`}
                            >
                              <MessageCircle className="w-3 h-3" />
                            </Button>
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => shareViaEmail(invite.inviteCode)}
                              data-testid={`button-email-${invite.id}`}
                            >
                              <Share2 className="w-3 h-3" />
                            </Button>
                            <Button
                              size="sm"
                              variant="destructive"
                              onClick={() => deactivateInviteMutation.mutate(invite.id)}
                              disabled={deactivateInviteMutation.isPending}
                              data-testid={`button-deactivate-${invite.id}`}
                            >
                              <X className="w-3 h-3" />
                            </Button>
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/RealTimeNotificati.content:
import { useState, useEffect } from "react";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Activity, X, Users, DollarSign, Clock } from "lucide-react";

interface NotificationItem {
  id: string;
  event: string;
  message: string;
  timestamp: string;
  type: 'transaction' | 'group' | 'member';
}

interface RealTimeNotificationsProps {
  isConnected: boolean;
}

export default function RealTimeNotifications({ isConnected }: RealTimeNotificationsProps) {
  const [notifications, setNotifications] = useState<NotificationItem[]>([]);
  const [isExpanded, setIsExpanded] = useState(false);

  useEffect(() => {
    // Listen for custom events from the WebSocket hook
    const handleNotification = (event: CustomEvent) => {
      const { event: eventType, data, timestamp } = event.detail;

      let message = '';
      let type: 'transaction' | 'group' | 'member' = 'transaction';

      switch (eventType) {
        case 'transaction_created':
          message = `New ${data.type}: ${data.description} (${data.amount})`;
          type = 'transaction';
          break;
        case 'group_created':
          message = `Group "${data.name}" created`;
          type = 'group';
          break;
        case 'group_member_added':
          message = `${data.member.name} joined a group`;
          type = 'member';
          break;
        default:
          return;
      }

      const notification: NotificationItem = {
        id: `${eventType}-${Date.now()}`,
        event: eventType,
        message,
        timestamp,
        type
      };

      setNotifications(prev => [notification, ...prev.slice(0, 9)]); // Keep only 10 most recent
    };

    window.addEventListener('websocket-notification', handleNotification as EventListener);

    return () => {
      window.removeEventListener('websocket-notification', handleNotification as EventListener);
    };
  }, []);

  const clearNotifications = () => {
    setNotifications([]);
  };

  const getIcon = (type: string) => {
    switch (type) {
      case 'transaction': return <DollarSign className="w-4 h-4" />;
      case 'group': return <Users className="w-4 h-4" />;
      case 'member': return <Users className="w-4 h-4" />;
      default: return <Activity className="w-4 h-4" />;
    }
  };

  const getBadgeVariant = (type: string) => {
    switch (type) {
      case 'transaction': return 'default';
      case 'group': return 'secondary';
      case 'member': return 'outline';
      default: return 'default';
    }
  };

  const formatTime = (timestamp: string) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  if (!isExpanded && notifications.length === 0) {
    return null;
  }

  return (
    <Card className="mb-6">
      <CardHeader className="pb-3">
        <CardTitle className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Activity className="w-5 h-5" />
            <span>Real-time Activity</span>
            {notifications.length > 0 && (
              <Badge variant="secondary">{notifications.length}</Badge>
            )}
          </div>
          <div className="flex items-center space-x-2">
            {notifications.length > 0 && (
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={clearNotifications}
                data-testid="button-clear-notifications"
              >
                Clear
              </Button>
            )}
            <Button 
              variant="ghost" 
              size="sm" 
              onClick={() => setIsExpanded(!isExpanded)}
              data-testid="button-toggle-notifications"
            >
              {isExpanded ? <X className="w-4 h-4" /> : <Activity className="w-4 h-4" />}
            </Button>
          </div>
        </CardTitle>
      </CardHeader>

      {isExpanded && (
        <CardContent className="pt-0">
          {!isConnected && (
            <div className="text-center py-4 text-amber-600 bg-amber-50 rounded-lg mb-4">
              <Clock className="w-6 h-6 mx-auto mb-2" />
              <p className="text-sm">Waiting for real-time connection...</p>
            </div>
          )}

          {notifications.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <Activity className="w-12 h-12 mx-auto mb-4 text-gray-300" />
              <p>No recent activity</p>
              <p className="text-sm">Real-time updates will appear here</p>
            </div>
          ) : (
            <ScrollArea className="h-48">
              <div className="space-y-3">
                {notifications.map((notification) => (
                  <div 
                    key={notification.id} 
                    className="flex items-start space-x-3 p-3 bg-gray-50 rounded-lg"
                    data-testid={`notification-${notification.type}`}
                  >
