client/src/components/ProfileInitializer.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { User, Globe, Settings } from "lucide-react";
import type { InsertUserProfile } from "@shared/schema";

// Pakistan-friendly defaults
const PAKISTAN_DEFAULTS = {
  currency: "PKR",
  language: "en",
  timezone: "Asia/Karachi",
  dateFormat: "DD/MM/YYYY",
  numberFormat: "en-PK",
  theme: "light",
  notifications: true,
  emailNotifications: false,
};

const CURRENCIES = [
  { value: "PKR", label: "‚Ç® Pakistani Rupee", symbol: "‚Ç®" },
  { value: "USD", label: "US Dollar ($)", symbol: "$" },
  { value: "EUR", label: "Euro (‚Ç¨)", symbol: "‚Ç¨" },
  { value: "SAR", label: "Saudi Riyal (Ô∑º)", symbol: "Ô∑º" },
  { value: "AED", label: "UAE Dirham (ÿØ.ÿ•)", symbol: "ÿØ.ÿ•" },
  { value: "INR", label: "Indian Rupee (‚Çπ)", symbol: "‚Çπ" },
];

const LANGUAGES = [
  { value: "en", label: "English" },
  { value: "ur", label: "ÿßÿ±ÿØŸà (Urdu)" },
  { value: "ar", label: "ÿßŸÑÿπÿ±ÿ®Ÿäÿ© (Arabic)" },
  { value: "hi", label: "‡§π‡§ø‡§Ç‡§¶‡•Ä (Hindi)" },
];

interface ProfileInitializerProps {
  children: React.ReactNode;
}

export function ProfileInitializer({ children }: ProfileInitializerProps) {
  const { user, isLoading, isAuthenticated } = useAuth();
  const [isOpen, setIsOpen] = useState(false);
  const [formData, setFormData] = useState<Partial<InsertUserProfile>>({
    publicName: "",
    ...PAKISTAN_DEFAULTS,
  });
  const { toast } = useToast();

  // Show dialog if authenticated but no profile exists
  useEffect(() => {
    if (!isLoading && isAuthenticated && user && !user.profile) {
      setIsOpen(true);
      // Pre-fill with user data
      const displayName = user.firstName ? `${user.firstName} ${user.lastName || ''}`.trim() : user.email?.split('@')[0] || 'User';
      setFormData(prev => ({
        ...prev,
        publicName: displayName,
      }));
    }
  }, [isLoading, isAuthenticated, user]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.publicName?.trim()) {
      toast({
        title: "Name Required",
        description: "Please enter your name to continue.",
        variant: "destructive",
      });
      return;
    }

    try {
      const response = await fetch('/api/profile', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error('Failed to create profile');

      setIsOpen(false);
      toast({
        title: "Welcome!",
        description: `Profile created successfully. Welcome to ExpenseShare, ${formData.publicName}!`,
      });

      // Refresh to get updated user data
      window.location.reload();
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to create profile. Please try again.",
        variant: "destructive",
      });
    }
  };

  const updateFormData = (key: keyof InsertUserProfile, value: any) => {
    setFormData(prev => ({ ...prev, [key]: value }));
  };

  // Show children only when authenticated and profile exists
  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-green-500 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading your profile...</p>
        </div>
      </div>
    );
  }

  // Don't render profile setup for non-authenticated users
  if (!isAuthenticated) {
    return <>{children}</>;
  }

  // Show profile setup if authenticated but no profile
  if (isAuthenticated && user && !user.profile) {
    return (
      <Dialog open={isOpen} onOpenChange={() => {}}>
        <DialogContent className="max-w-2xl" onPointerDownOutside={(e) => e.preventDefault()}>
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <User className="w-5 h-5" />
              Welcome to ExpenseShare
            </DialogTitle>
            <DialogDescription>
              Let's set up your profile with Pakistan-friendly defaults. You can change these settings anytime.
            </DialogDescription>
          </DialogHeader>

          <form onSubmit={handleSubmit} className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="text-lg flex items-center gap-2">
                  <User className="w-4 h-4" />
                  Basic Information
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="publicName">Your Name *</Label>
                  <Input
                    id="publicName"
                    placeholder="Enter your full name"
                    value={formData.publicName || ""}
                    onChange={(e) => updateFormData("publicName", e.target.value)}
                    required
                    data-testid="input-setup-name"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    type="email"
                    value={user?.email || ""}
                    disabled
                    className="bg-gray-100 dark:bg-gray-800"
                    data-testid="input-setup-email"
                  />
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="text-lg flex items-center gap-2">
                  <Globe className="w-4 h-4" />
                  Regional Preferences
                </CardTitle>
                <p className="text-sm text-muted-foreground mt-2">
                  Pre-configured with Pakistan-friendly defaults
                </p>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="currency">Currency</Label>
                    <Select
                      value={formData.currency || "PKR"}
                      onValueChange={(value) => updateFormData("currency", value)}
                    >
                      <SelectTrigger data-testid="select-setup-currency">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {CURRENCIES.map((currency) => (
                          <SelectItem key={currency.value} value={currency.value}>
                            {currency.symbol} {currency.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="language">Language</Label>
                    <Select
                      value={formData.language || "en"}
                      onValueChange={(value) => updateFormData("language", value)}
                    >
                      <SelectTrigger data-testid="select-setup-language">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {LANGUAGES.map((language) => (
                          <SelectItem key={language.value} value={language.value}>
                            {language.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </div>
                <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                  <h4 className="font-medium text-blue-900 mb-2">Default Settings Applied:</h4>
                  <ul className="text-sm text-blue-800 space-y-1">
                    <li>‚Ä¢ Timezone: Pakistan Standard Time (Karachi)</li>
                    <li>‚Ä¢ Date Format: DD/MM/YYYY (Pakistani format)</li>
                    <li>‚Ä¢ Number Format: English (Pakistan)</li>
                  </ul>
                </div>
              </CardContent>
            </Card>

            <div className="flex justify-end gap-3">
              <Button
                type="submit"
                disabled={!formData.publicName?.trim()}
                data-testid="button-create-profile"
              >
                <Settings className="w-4 h-4 mr-2" />
                Create Profile
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    );
  }

  return <>{children}</>;
}

client/src/components/SettingsModal.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Settings, User, Globe, Bell, Palette, Save } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import type { UserProfile, InsertUserProfile } from "@shared/schema";

interface SettingsModalProps {
  children: React.ReactNode;
}

// Pakistan-friendly currency options
const CURRENCIES = [
  { value: "PKR", label: "Pakistani Rupee (‚Ç®)", symbol: "‚Ç®" },
  { value: "USD", label: "US Dollar (USD)", symbol: "$" },
  { value: "EUR", label: "Euro (EUR)", symbol: "‚Ç¨" },
  { value: "GBP", label: "British Pound (GBP)", symbol: "¬£" },
  { value: "SAR", label: "Saudi Riyal (SAR)", symbol: "Ô∑º" },
  { value: "AED", label: "UAE Dirham (AED)", symbol: "ÿØ.ÿ•" },
  { value: "INR", label: "Indian Rupee (INR)", symbol: "‚Çπ" },
];

// Language options with Pakistan-friendly defaults
const LANGUAGES = [
  { value: "en", label: "English" },
  { value: "ur", label: "ÿßÿ±ÿØŸà (Urdu)" },
  { value: "ar", label: "ÿßŸÑÿπÿ±ÿ®Ÿäÿ© (Arabic)" },
  { value: "hi", label: "‡§π‡§ø‡§Ç‡§¶‡•Ä (Hindi)" },
];

// Date format options
const DATE_FORMATS = [
  { value: "DD/MM/YYYY", label: "DD/MM/YYYY (Pakistani)" },
  { value: "MM/DD/YYYY", label: "MM/DD/YYYY (US)" },
  { value: "YYYY-MM-DD", label: "YYYY-MM-DD (ISO)" },
];

// Timezone options for Pakistan region
const TIMEZONES = [
  { value: "Asia/Karachi", label: "Pakistan Standard Time (Karachi)" },
  { value: "Asia/Lahore", label: "Pakistan Standard Time (Lahore)" },
  { value: "Asia/Dubai", label: "Gulf Standard Time (Dubai)" },
  { value: "Asia/Riyadh", label: "Arabia Standard Time (Riyadh)" },
  { value: "UTC", label: "UTC" },
];

export function SettingsModal({ children }: SettingsModalProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [profile, setProfile] = useState<Partial<InsertUserProfile>>({
    publicName: "",
    email: "",
    currency: "PKR",
    language: "en",
    timezone: "Asia/Karachi",
    dateFormat: "DD/MM/YYYY",
    numberFormat: "en-PK",
    theme: "light",
    notifications: true,
    emailNotifications: false,
  });
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch current profile
  const { data: currentProfile, isLoading } = useQuery({
    queryKey: ['/api/profile'],
    enabled: isOpen,
  });

  // Update profile when data is loaded
  useEffect(() => {
    if (currentProfile) {
      setProfile(currentProfile);
    }
  }, [currentProfile]);

  // Save profile mutation
  const saveProfileMutation = useMutation({
    mutationFn: async (data: Partial<InsertUserProfile>) => {
      if ((currentProfile as any)?.id) {
        return await apiRequest('PATCH', `/api/profile/${(currentProfile as any).id}`, data);
      } else {
        return await apiRequest('POST', '/api/profile', data);
      }
    },
    onSuccess: () => {
      toast({
        title: "Settings Saved",
        description: "Your preferences have been updated successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/profile'] });
      setIsOpen(false);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to save settings. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSave = () => {
    if (!profile.publicName?.trim()) {
      toast({
        title: "Name Required",
        description: "Please enter your public name.",
        variant: "destructive",
      });
      return;
    }

    saveProfileMutation.mutate(profile);
  };

  const updateProfile = (key: keyof InsertUserProfile, value: any) => {
    setProfile(prev => ({ ...prev, [key]: value }));
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        {children}
      </DialogTrigger>
      <DialogContent className="max-w-3xl max-h-[85vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Settings className="w-5 h-5" />
            Settings & Preferences
          </DialogTitle>
          <DialogDescription>
            Customize your experience with currency, language, and notification preferences
          </DialogDescription>
        </DialogHeader>

        <Tabs defaultValue="profile" className="space-y-4">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="profile" className="flex items-center gap-2">
              <User className="w-4 h-4" />
              Profile
            </TabsTrigger>
            <TabsTrigger value="regional" className="flex items-center gap-2">
              <Globe className="w-4 h-4" />
              Regional
            </TabsTrigger>
            <TabsTrigger value="notifications" className="flex items-center gap-2">
              <Bell className="w-4 h-4" />
              Notifications
            </TabsTrigger>
            <TabsTrigger value="appearance" className="flex items-center gap-2">
              <Palette className="w-4 h-4" />
              Appearance
            </TabsTrigger>
          </TabsList>

          {/* Profile Settings */}
          <TabsContent value="profile">
            <Card>
              <CardHeader>
                <CardTitle>Profile Information</CardTitle>
                <CardDescription>
                  Your public information used in groups and transactions
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="publicName">Public Name *</Label>
                  <Input
                    id="publicName"
                    placeholder="Enter your display name"
                    value={profile.publicName || ""}
                    onChange={(e) => updateProfile("publicName", e.target.value)}
                    data-testid="input-public-name"
                  />
                  <p className="text-sm text-muted-foreground">
                    This name will be visible to other group members
                  </p>
                </div>
                <div className="space-y-2">
                  <Label htmlFor="email">Email (Optional)</Label>
                  <Input
                    id="email"
                    type="email"
                    placeholder="your.email@example.com"
                    value={profile.email || ""}
                    onChange={(e) => updateProfile("email", e.target.value)}
                    data-testid="input-email"
                  />
                  <p className="text-sm text-muted-foreground">
                    Used for notifications and account recovery
                  </p>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* Regional Settings */}
          <TabsContent value="regional">
            <div className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle>Currency & Format</CardTitle>
                  <CardDescription>
                    Set your preferred currency and number formatting
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="currency">Primary Currency</Label>
                    <Select
                      value={profile.currency || "PKR"}
                      onValueChange={(value) => updateProfile("currency", value)}
                    >
                      <SelectTrigger data-testid="select-currency">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {CURRENCIES.map((currency) => (
                          <SelectItem key={currency.value} value={currency.value}>
                            {currency.symbol} {currency.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="dateFormat">Date Format</Label>
                    <Select
                      value={profile.dateFormat || "DD/MM/YYYY"}
                      onValueChange={(value) => updateProfile("dateFormat", value)}
                    >
                      <SelectTrigger data-testid="select-date-format">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {DATE_FORMATS.map((format) => (
                          <SelectItem key={format.value} value={format.value}>
                            {format.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle>Language & Region</CardTitle>
                  <CardDescription>
                    Choose your language and timezone preferences
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="language">Language</Label>
                    <Select
                      value={profile.language || "en"}
                      onValueChange={(value) => updateProfile("language", value)}
                    >
                      <SelectTrigger data-testid="select-language">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {LANGUAGES.map((language) => (
                          <SelectItem key={language.value} value={language.value}>
                            {language.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="timezone">Timezone</Label>
                    <Select
                      value={profile.timezone || "Asia/Karachi"}
                      onValueChange={(value) => updateProfile("timezone", value)}
                    >
                      <SelectTrigger data-testid="select-timezone">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {TIMEZONES.map((timezone) => (
                          <SelectItem key={timezone.value} value={timezone.value}>
                            {timezone.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </CardContent>
              </Card>
            </div>
          </TabsContent>

          {/* Notification Settings */}
          <TabsContent value="notifications">
            <Card>
              <CardHeader>
                <CardTitle>Notification Preferences</CardTitle>
                <CardDescription>
                  Control how and when you receive notifications
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label>Real-time Notifications</Label>
                    <p className="text-sm text-muted-foreground">
                      Get instant notifications for group activity
                    </p>
                  </div>
                  <Switch
                    checked={profile.notifications ?? true}
                    onCheckedChange={(checked) => updateProfile("notifications", checked)}
                    data-testid="switch-notifications"
                  />
                </div>
                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label>Email Notifications</Label>
                    <p className="text-sm text-muted-foreground">
                      Receive expense summaries and important updates via email
                    </p>
                  </div>
                  <Switch
                    checked={profile.emailNotifications ?? false}
                    onCheckedChange={(checked) => updateProfile("emailNotifications", checked)}
                    data-testid="switch-email-notifications"
                  />
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* Appearance Settings */}
          <TabsContent value="appearance">
            <Card>
              <CardHeader>
                <CardTitle>Theme & Appearance</CardTitle>
                <CardDescription>
                  Customize the look and feel of the application
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="theme">Theme</Label>
                  <Select
                    value={profile.theme || "light"}
                    onValueChange={(value) => updateProfile("theme", value)}
                  >
                    <SelectTrigger data-testid="select-theme">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="light">Light</SelectItem>
                      <SelectItem value="dark">Dark</SelectItem>
                      <SelectItem value="system">System</SelectItem>
                    </SelectContent>
                  </Select>
                  <p className="text-sm text-muted-foreground">
                    Choose your preferred color scheme
                  </p>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>

        <div className="flex justify-end gap-3 pt-4 border-t">
          <Button variant="outline" onClick={() => setIsOpen(false)}>
            Cancel
          </Button>
          <Button 
            onClick={handleSave}
            disabled={saveProfileMutation.isPending}
            data-testid="button-save-settings"
          >
            {saveProfileMutation.isPending ? (
              "Saving..."
            ) : (
              <>
                <Save className="w-4 h-4 mr-2" />
                Save Settings
              </>
            )}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

